// Exercise 3
// Change the overview to support an edit mode. The overview will have a button to toggle 
// between view and edit mode. When in edit mode the overview of people has an extra X button
// to remove a people from the in-memory storage.


import React from "react"
import { PersonEntry } from "../Home/home.state"


export interface OverviewProps{
    storage: Map<number,PersonEntry>
    backToHome: () => void
    //Exercise 3
    removePerson: (id:number) => void
}


export const Overview = (props:OverviewProps) : JSX.Element => (
    <div>
        <div> 
            Overview of the Registration 
        </div>
        {
            Array.from(props.storage.values()).map(
                person => (
                    <div key={`${person.id}`}>
                        Name: {person.name}  
                        Last Name: {person.lastname } 
                        Age: {person.age } 
                    </div>
            ))
        }

        <div>
            <button
                onClick={_ => props.backToHome()}
            >
                Back
            </button>
        </div>
    </div>
)
