export interface Person{
    name:string
    lastname:string
    age:number
}

export type PersonEntry = Person & {id:number}

export type RegistrationFormState=Person & {
//export interface RegistrationFormState{
    //name:string
    //lastname:string
    //age:number
    //updateName: (e:React.ChangeEvent<HTMLInputElement>)=>(state:RegistrationFormState) => RegistrationFormState
    storage :Map<number,PersonEntry>
    updateName: (name:string) => (state:RegistrationFormState) =>  RegistrationFormState
    updateLastName: (lastname:string) => (state:RegistrationFormState) =>  RegistrationFormState
    updateAge: (age:number) => (state:RegistrationFormState) =>  RegistrationFormState
}

export const initRegistrationState :RegistrationFormState={
    name: "",
    lastname:"",
    age:18,
    storage: new Map(),
    //updateName: (e:React.ChangeEvent<HTMLInputElement>) => (state:RegistrationFormState): RegistrationFormState =>({
    //    ...state,
    //    name: e.currentTarget.value
    //})
    updateName: (name:string) => (state:RegistrationFormState): RegistrationFormState =>({
        ...state,
        name: name
    }),
    updateLastName: (lastname:string) => (state:RegistrationFormState): RegistrationFormState =>({
        ...state,
        lastname: lastname
    }),
    updateAge: (age:number) => (state:RegistrationFormState): RegistrationFormState =>({
        ...state,
        age: age
    })
}
