import React from "react";
import { initRegistrationState, RegistrationFormState } from "./registration.state";
import { Person } from "../Home/home.state";


export interface RegistrationProps{
    insertPerson:(person:Person) =>void
}

//Add react component 

export class RegistrationForm extends React.Component<RegistrationProps,RegistrationFormState>{
    constructor(props: RegistrationProps){
        super(props)//base class
        //initialize the state
        //this.state = {}
        this.state = initRegistrationState //invoke 

    }

    //each react component has a render method
    //seo each component should implement the render method

    //JSX.Element is an object in react that represent an html node
    //When ever the render is called namely fomr the index.tsx as root.render
    //it will go though each component and these are converted to html elements.


    //Hello REact!
    /*
    render():JSX.Element{
        return(
            <div>
                Hello Registration!
            </div>
        )
    }
    */

    render():JSX.Element{
        return(
            <div>
                <div>
                    Name:
                    <input
                        value={this.state.name}
                        //onChange={e => this.setState({...this.state, name: e.currentTarget.value})}
                        //onChange={e=> this.setState((state: RegistrationFormState) => ({...this.state, name: e.currentTarget.value}))}

                        //onChange={e=> this.setState(this.state.updateName(e))}
                        //This will guarantee that the state will be the latest possible version
                        /*React at some point combine all these callbacks into a single callback and
                        it will update all the states in sequence and it will inject the lastest state
                        into the next call back that react has to run.
                        There is a cascase od callbacka that will be executed in sequence.
                        But the setstate runs immmmidiately*/

                        onChange={e=> this.setState(this.state.updateName(e.currentTarget.value))}
                    >
                    </input>
                </div>
                <div>
                    Last Name:
                    <input
                        value={this.state.lastname}
                        onChange={e=> this.setState(this.state.updateLastName(e.currentTarget.value))}
                    >
                    </input>
                </div>
                <div>
                    Age:
                    <input
                        value={this.state.age}
                        type="number"
                        onChange={e=> this.setState(this.state.updateAge(e.currentTarget.valueAsNumber))}
                    >
                    </input>
                </div>
                <div>
                    <button
                        onClick={_ => this.props.insertPerson({
                            name:this.state.name,
                            lastname:this.state.lastname,
                            age:this.state.age
                        })}
                    >
                        Submit
                    </button>
                </div>
            </div>
        )
    }
}
