export interface RegistrationFormState{
    name:string
    lastname:string
    age:number
    updateName: (e:React.ChangeEvent<HTMLInputElement>)=>(state:RegistrationFormState) => RegistrationFormState
}

export const initRegistrationState :RegistrationFormState={
    name: "",
    lastname:"",
    age:18,
    updateName: (e:React.ChangeEvent<HTMLInputElement>) => (state:RegistrationFormState): RegistrationFormState =>({
        ...state,
        name: e.currentTarget.value
    })
}
